CREATE TABLE IF NOT EXISTS schedule (schedule_id INTEGER NOT NULL PRIMARY KEY, company_name TEXT NOT NULL, place TEXT NOT NULL, date TEXT, alarm_time, time TEXT);
CREATE TABLE IF NOT EXISTS topic (topic_id INTEGER NOT NULL PRIMARY KEY, author TEXT, title TEXT, time TEXT); 
CREATE TABLE IF NOT EXISTS career_talk (career_talk_id INTEGER NOT NULL PRIMARY KEY, topic_id INTEGER, company_id INTEGER, company_name TEXT, company_type TEXT, company_industry TEXT, company_place TEXT, company_introduction TEXT, school_name TEXT,place TEXT,time TEXT, date TEXT,introduction TEXT, clicks INTEGER, joins INTEGER, replies INTEGER, isjoined INTEGER);
CREATE TABLE IF NOT EXISTS recruit (recruit_id INTEGER NOT NULL PRIMARY KEY, topic_id INTEGER, clicks INTEGER, source TEXT, company_id INTEGER, company_name TEXT, company_type TEXT, company_place TEXT, company_industry TEXT, company_introduction TEXT, contact TEXT, position TEXT, type Integer, time TEXT, place TEXT, joins INTEGER, replies INTEGER, description TEXT, isjoined INTEGER);
CREATE TABLE IF NOT EXISTS message(user_id TEXT NOT NULL PRIMARY KEY, user_name TEXT, last_message TEXT, last_time TEXT);
